import React, { useState } from "react";
import { FiSend } from "react-icons/fi";

const App = () => {
  const [userName, setUserName] = useState("");
  const [isNameEntered, setIsNameEntered] = useState(false);
  const [selectedRepo, setSelectedRepo] = useState("");
  const [searchInput, setSearchInput] = useState("");

  const repositories = [
    "React Project",
    "Node.js Backend",
    "Python Script",
    "Machine Learning Model",
    "Full-Stack App",
  ];

  const handleNameSubmit = () => {
    if (userName.trim()) {
      setIsNameEntered(true);
    }
  };

  const handleAction = () => {
    if (searchInput || selectedRepo) {
      alert(`Analyzing "${selectedRepo}" with input: "${searchInput}"`);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center">
      {/* Header */}
      <h1 className="text-4xl font-bold mb-6 text-blue-700">
        AI Repository Analyzer
      </h1>

      {!isNameEntered ? (
        // Name Input Section
        <div className="w-full max-w-md">
          <input
            type="text"
            placeholder="Enter your name"
            className="w-full p-3 border border-gray-300 rounded focus:outline-blue-500"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
          />
          <button
            onClick={handleNameSubmit}
            className="mt-4 w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600 transition-all"
          >
            Continue
          </button>
        </div>
      ) : (
        // Dropdown + Search Section
        <div className="w-full max-w-md">
          <div className="flex items-center border border-gray-300 rounded overflow-hidden">
            <select
              className="p-3 bg-white flex-grow focus:outline-none"
              value={selectedRepo}
              onChange={(e) => setSelectedRepo(e.target.value)}
            >
              <option value="">Select Repository</option>
              {repositories.map((repo, index) => (
                <option key={index} value={repo}>
                  {repo}
                </option>
              ))}
            </select>
            <input
              type="text"
              placeholder="Type a query..."
              className="flex-grow p-3 border-l focus:outline-none"
              value={searchInput}
              onChange={(e) => setSearchInput(e.target.value)}
            />
            <button
              onClick={handleAction}
              className="bg-blue-500 p-3 text-white hover:bg-blue-600 transition-all"
            >
              <FiSend size={20} />
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
