import React, { useState } from "react";
import axios from "axios";

function App() {
  const [githubLink, setGithubLink] = useState("");
  const [menuOption1, setMenuOption1] = useState("Bug Detection");
  const [menuOption2, setMenuOption2] = useState("JavaScript");
  const [menuOption3, setMenuOption3] = useState("Frontend");
  const [result, setResult] = useState("");

  const handleAnalyze = async () => {
    try {
      const response = await axios.post("http://localhost:5000/analyze", {
        githubLink,
        menuOption1,
        menuOption2,
        menuOption3,
      });
      setResult(response.data.result);
    } catch (error) {
      console.error("Error analyzing code:", error);
      setResult("An error occurred. Please try again.");
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-4">
      <h1 className="text-3xl font-bold mb-6 text-blue-700">AI Code Analyzer</h1>
      <div className="w-full max-w-md bg-white p-6 rounded-lg shadow-lg">
        <input
          type="text"
          placeholder="Enter GitHub Repository Link"
          value={githubLink}
          onChange={(e) => setGithubLink(e.target.value)}
          className="w-full p-2 border border-gray-300 rounded mb-4 focus:outline-blue-500"
        />
        <div className="grid grid-cols-1 gap-4">
          <select
            value={menuOption1}
            onChange={(e) => setMenuOption1(e.target.value)}
            className="p-2 border border-gray-300 rounded focus:outline-blue-500"
          >
            <option>Bug Detection</option>
            <option>Function Suggestion</option>
            <option>Optimization</option>
          </select>
          <select
            value={menuOption2}
            onChange={(e) => setMenuOption2(e.target.value)}
            className="p-2 border border-gray-300 rounded focus:outline-blue-500"
          >
            <option>JavaScript</option>
            <option>Python</option>
            <option>Java</option>
          </select>
          <select
            value={menuOption3}
            onChange={(e) => setMenuOption3(e.target.value)}
            className="p-2 border border-gray-300 rounded focus:outline-blue-500"
          >
            <option>Frontend</option>
            <option>Backend</option>
            <option>Full Stack</option>
          </select>
        </div>
        <button
          onClick={handleAnalyze}
          className="w-full bg-blue-500 text-white p-2 rounded mt-4 hover:bg-blue-600 transition-all"
        >
          Analyze
        </button>
        <div className="mt-4">
          <h3 className="font-bold text-gray-800">Result:</h3>
          <p className="text-gray-700">{result}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
